import React, { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
export default function EditTrainee(props){
    const {id}=useParams();
    const [fullname,setFullname]=useState(props.Trainees.filter((e)=>e.id==parseInt(id)).map((e)=>e.fullname));
    const [mail,setMail]=useState(props.Trainees.filter((e)=>e.id==parseInt(id)).map((e)=>e.mail));
    const nav=useNavigate();

        
    function handleSubmit(event){
        event.preventDefault();
        const newTrainee={
            id:id,
            fullname:fullname,
            mail:mail
        }
        
        props.childToParent1(newTrainee)
        
        nav("/")
    }

    return(
        <>
        <center>
        <form onSubmit={handleSubmit} className="form-group pt-5 mt-5">
            <fieldset>
                <legend>Insert the trainne's data : </legend>
                <table>
                    <tr>
                        <td><label for="fullname">Fullname :</label></td>
                        <td><input className="form-control" value={fullname} type="text" onChange={(e)=>setFullname(e.target.value)}/></td>
                    </tr>
                    <tr>
                        <td><label for="email">Email :</label></td>
                        <td><input className="form-control" value={mail} type="text" onChange={(e)=>setMail(e.target.value)}/></td>
                    </tr>
                    <tr>
                        <td colSpan="2"><input type="Submit" value="Submit Edits" className="btn btn-danger" /></td>
                    </tr>
                </table>
            </fieldset>
        </form></center>
        </>
    )
}
