import React, { useState } from "react";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import ListInfo from "./ListInfo";
import AjouterStg from "./AjouterStg";
import Update from "./Update";

const CheminCr=()=>{
    const [list,setList]=useState([
        {
            id:1,
            image:"femme1.PNG",
            prenom:"Chaimae",
            nom:"Chaire",
            email:"chaimaechaire@gmail.com"
        }

    ]);
    function ajouterstg(nouveaustg){
        setList([...list,nouveaustg])

    }
    return(
    <BrowserRouter>
        <Routes>
            <Route path="/" element={<ListInfo List={list} />}/>
            <Route path="/AjouterStg" element={<AjouterStg ajouterstg={ajouterstg} List={list}/>}/>
            <Route path="Update/:id" element={<Update/>}/>
        </Routes>
    </BrowserRouter>
    )

}
export default CheminCr ;
