import React, { useState } from "react";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import ListInfo from "./ListInfo";
import AjouterStg from "./AjouterStg";
import Update from "./Update";

const CheminCr=()=>{
    const [list,setList]=useState([
        {
            id:1,
            image:"femme1.PNG",
            prenom:"Chaimae",
            nom:"Chaire",
            email:"chaimaechaire@gmail.com"
        }

    ]);
    function ajouterstg(nouveaustg){
        setList([...list,nouveaustg])
    }
    function Updatestg(updatestg){
        const udpatstgn=list.filter((s)=>s.id==updatestg.id)
        console.log(updatestg)
        udpatstgn.image=updatestg.image;
        udpatstgn.nom=updatestg.nom;
        udpatstgn.prenom=updatestg.prenom;
        udpatstgn.email=updatestg.email;

    }
    return(
    <BrowserRouter>
        <Routes>
            <Route path="/" element={<ListInfo List={list} />}/>
            <Route path="/AjouterStg" element={<AjouterStg ajouterstg={ajouterstg} List={list}/>}/>
            <Route path="Update/:id" element={<Update  List={list} Updatestg={Updatestg}/>}/>
        </Routes>
    </BrowserRouter>
    )

}
export default CheminCr ;
