import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
export default function AddTrainee(props){
    
    const [fullname,setFullname]=useState("");
    const [mail,setMail]=useState("");
    const nav=useNavigate();

    function handleSubmit(event){
        event.preventDefault();
        const newTrainee={
            id:0,
            fullname:fullname,
            mail:mail
        }
        
        props.childToParent(newTrainee)
        
        nav("/")
    }

    return(
        <>

        <center><form onSubmit={handleSubmit} className="form-group pt-5 mt-5">
            <fieldset>
                <legend>Insert the trainne's data : </legend>
                <table>
                    <tr>
                        <td><label for="fullname">Fullname :</label></td>
                        <td><input className="form-control" type="text" onChange={(e)=>setFullname(e.target.value)}/></td>
                    </tr>
                    <tr>
                        <td><label for="email">Email :</label></td>
                        <td><input className="form-control" type="text" onChange={(e)=>setMail(e.target.value)}/></td>
                    </tr>
                    <tr>
                        <td colSpan="2"><input type="Submit" value="Submit" className="btn btn-danger" /></td>
                    </tr>
                </table>
            </fieldset>
        </form></center>
        </>
    )
}
