import React, { useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
const Update=(props)=>{
    const{id}=useParams()
    const [image,setimage]=useState(props.List.filter((s)=>s.id==id).map((e)=>e.image))
    const [prenom,setprenom]=useState(props.List.filter((s)=>s.id==id).map((e)=>e.prenom))
    const [nom,setnom]=useState(props.List.filter((s)=>s.id==id).map((e)=>e.nom))
    const [email,setemail]=useState(props.List.filter((s)=>s.id==id).map((e)=>e.email))
    const navigate=useNavigate();
    function Updatestg(e){
        e.preventDefault();
        const nouveaustg={
            id:id,
            image:image,
            nom:nom,
            prenom:prenom,
            email:email
        }
        props.Updatestg(nouveaustg)
        navigate("/")
    }
    return(
            <form onSubmit={Updatestg}>
                <fieldset>
                    <label>Image : </label>
                    <input type="text" id="image" value={image} onChange={((e)=>setimage(e.target.value))}/>
                    <br/>
                    <label>Prenom : </label>
                    <input type="text" id="prenom" value={prenom} onChange={((e)=>setprenom(e.target.value))}/>
                    <br/>
                    <label>Nom : </label>
                    <input type="text" id="nom" value={nom} onChange={((e)=>setnom(e.target.value))}/>
                    <br/>
                    <label>Email : </label>
                    <input type="text" id="email"  value={email} onChange={((e)=>setemail(e.target.value))}/>
                    <br/>
                    <button type="submit" >Modifier </button>
                </fieldset>
            </form>
    )
}
export default Update
