import React from "react";
import { Link } from "react-router-dom";

export default function ListTrainee(props) {
  const handleRemove = (id) => {
    const confirmation=window.confirm("Are you sure you want to delete that Trainee?")
    if (confirmation) {
        //const updatedTrainees = props.Trainees.filter((t) => t.id !== id);
        props.childToParent2(id);
    }
  };

  return (
    <>
      <h2>Trainees' list</h2>
      <Link to="/addtrainee">
        <button className="btn btn-danger">Add a new Trainee</button>
      </Link>
      <table className="table">
        <thead className="bg-light">
          <tr>
            <td>ID</td>
            <td>FULLNAME</td>
            <td>EMAIL</td>
            <td>EDIT</td>
            <td>REMOVE</td>
          </tr>
        </thead>
        <tbody>
          {props.Trainees.map((t) => (
            <tr key={t.id}>
              <td>{t.id}</td>
              <td>{t.fullname}</td>
              <td>{t.mail}</td>
              <td>
                <Link to={`/edittrainee/${t.id}`}>
                  <button className="btn btn-danger">Edit</button>
                </Link>
              </td>
              <td>
                <button className="btn btn-danger" onClick={() => handleRemove(t.id)}>Remove</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
}
