import React, { useState } from "react";
import { useNavigate } from "react-router-dom";


const AjouterStg=(props)=>{
    const [image,setimage]=useState()
    const [prenom,setprenom]=useState()
    const [nom,setnom]=useState()
    const [email,setemail]=useState()
    const navigate=useNavigate();
    function ajouterstg(e){
        e.preventDefault();
        const nouveaustg={
            id:props.List.length+1,
            image:image,
            nom:nom,
            prenom:prenom,
            email:email
        }
        props.ajouterstg(nouveaustg)
        navigate("/")
    }
    return(
            <form onSubmit={ajouterstg}>
                <fieldset>
                    <label>Image : </label>
                    <input type="text" id="image" onChange={((e)=>setimage(e.target.value))}/>
                    <br/>
                    <label>Prenom : </label>
                    <input type="text" id="prenom"onChange={((e)=>setprenom(e.target.value))}/>
                    <br/>
                    <label>Nom : </label>
                    <input type="text" id="nom"onChange={((e)=>setnom(e.target.value))}/>
                    <br/>
                    <label>Email : </label>
                    <input type="text" id="email" onChange={((e)=>setemail(e.target.value))}/>
                    <br/>
                    <button type="submit" >Ajouter </button>
                </fieldset>
            </form>
    )
}
export default AjouterStg
