import React from "react";
import "./crudStyle.css"
import { Link } from "react-router-dom";
const ListInfo=(props)=>{
    function removestg(id){
        const confirm=window.confirm("are you sure that remove this stagiaire?")
        if(confirm){
            props.removestg(id)
        }
    }


    return(
        <>
            <h1 className="center">List Des Stagiaires</h1>
            <Link to="/AjouterStg"><button type="button">Ajouter Stagiaire</button></Link>
            <div>
                {props.List.map((e)=>(
                    <div key={e.id}>
                        <img src={`/${e.image}`} alt="photo"></img>
                        <h4>id : {e.id}</h4>
                        <p>Prenom : {e.prenom} </p>
                        <p>Nom : {e.nom} </p>
                        <p>Email : {e.email}</p>
                        <Link to={`Update/${e.id}`}><button type="button">Modifier</button></Link>
                        <button type="button" onClick={()=>{removestg(e.id)}}>Supprimer</button>
                    </div>)
                )}

            </div>
        
        </>


    )




}
export default ListInfo
